import React, { useState, useEffect } from 'react';

const App = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState('');
  const [dailyVerse, setDailyVerse] = useState('');
  const [dailyReference, setDailyReference] = useState('');
  const [storyInput, setStoryInput] = useState('');
  const [storyline, setStoryline] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchDailyVerse = async () => {
      try {
        const response = await fetch('/api/daily-verse');
        const data = await response.json();
        if (data.success) {
          setDailyVerse(data.verse);
          setDailyReference(data.reference);
        } else {
          setDailyVerse('Unable to fetch daily verse.');
        }
      } catch (error) {
        setDailyVerse('Error fetching daily verse.');
      }
    };

    fetchDailyVerse();
  }, []);

  const handleSearch = async () => {
    if (!searchQuery.trim()) {
      setSearchResults('Please enter a keyword to search.');
      return;
    }
    setLoading(true);
    setSearchResults('');
    try {
      const response = await fetch('/api/search', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ query: searchQuery }),
      });
      const data = await response.json();
      if (data.success) {
        setSearchResults(data.results);
      } else {
        setSearchResults('No results found.');
      }
    } catch (error) {
      setSearchResults('Error fetching search results.');
    }
    setLoading(false);
  };

  const handleStoryline = async () => {
    if (!storyInput.trim()) {
      setStoryline('Please enter a verse or name to generate a story.');
      return;
    }
    setLoading(true);
    setStoryline('');
    try {
      const response = await fetch('/api/storyline', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ input: storyInput }),
      });
      const data = await response.json();
      if (data.success) {
        setStoryline(data.storyline);
      } else {
        setStoryline('Failed to generate storyline.');
      }
    } catch (error) {
      setStoryline('Error generating storyline.');
    }
    setLoading(false);
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>Bible App</h1>

      <div style={{ marginBottom: '20px' }}>
        <h2>Daily Verse</h2>
        <p>
          <strong>{dailyVerse}</strong> - <i>{dailyReference}</i>
        </p>
      </div>

      <div style={{ marginBottom: '20px' }}>
        <h2>Bible Search</h2>
        <input
          type="text"
          placeholder="Enter a keyword"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          style={{ width: '300px', padding: '10px' }}
        />
        <button onClick={handleSearch} disabled={loading} style={{ padding: '10px 20px', marginLeft: '10px' }}>
          {loading ? 'Searching...' : 'Search'}
        </button>
        {searchResults && <p>{searchResults}</p>}
      </div>

      <div>
        <h2>Storyline for Kids</h2>
        <input
          type="text"
          placeholder="Enter a verse or name"
          value={storyInput}
          onChange={(e) => setStoryInput(e.target.value)}
          style={{ width: '300px', padding: '10px' }}
        />
        <button onClick={handleStoryline} disabled={loading} style={{ padding: '10px 20px', marginLeft: '10px' }}>
          {loading ? 'Generating...' : 'Generate Story'}
        </button>
        {storyline && <p>{storyline}</p>}
      </div>
    </div>
  );
};

export default App;
