import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const apiKey = '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';

  const handleSearch = async () => {
    setLoading(true);
    setError('');
    setResults([]);

    try {
      const response = await axios.post(
        'https://api.openai.com/v1/completions',
        {
          model: 'text-davinci-003',
          prompt: `Provide Bible verses related to: "${query}".`,
          max_tokens: 150,
          temperature: 0.7,
        },
        {
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${apiKey}`,
          },
        }
      );

      setResults(response.data.choices[0].text.trim().split('\n').filter((line) => line));
    } catch (err) {
      console.error(err);
      setError('Failed to fetch data. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>Bible Search</h1>
      <input
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Type a keyword (e.g., love, forgiveness)"
        style={{ width: '300px', padding: '10px', marginBottom: '10px' }}
      />
      <button onClick={handleSearch} style={{ padding: '10px 20px', marginLeft: '10px' }}>
        Search
      </button>
      {loading && <p>Loading...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <div>
        <h2>Results:</h2>
        {results.length > 0 ? (
          <ul>
            {results.map((result, index) => (
              <li key={index}>{result}</li>
            ))}
          </ul>
        ) : (
          !loading && <p>No results found.</p>
        )}
      </div>
    </div>
  );
}

export default App;
