import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState("");
  const [dailyQuote, setDailyQuote] = useState("Loading daily quote...");
  const [storyQuery, setStoryQuery] = useState("");
  const [storyResult, setStoryResult] = useState("");

  // Fetch Daily Quote
  useEffect(() => {
    const fetchQuote = async () => {
      try {
        const response = await fetch("https://labs.bible.org/api/?passage=random&type=json");
        const data = await response.json();
        if (data.length > 0) {
          const { bookname, chapter, verse, text } = data[0];
          setDailyQuote(`${text} (${bookname} ${chapter}:${verse})`);
        } else {
          setDailyQuote("No quote available today. Please try again later.");
        }
      } catch (error) {
        console.error("Error fetching daily quote:", error);
        setDailyQuote("Error fetching daily quote. Please try again later.");
      }
    };

    fetchQuote();
  }, []);

  // Handle Bible Search
  const handleSearch = async () => {
    try {
      const response = await fetch("http://localhost:3001/api/search", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ query: searchQuery }),
      });
      const data = await response.json();
      if (data.success) {
        setSearchResults(data.results);
      } else {
        setSearchResults("No results found.");
      }
    } catch (error) {
      console.error("Error fetching search results:", error);
      setSearchResults("Error fetching results. Please try again.");
    }
  };

  // Handle Story Generation
  const handleStoryRequest = async () => {
    if (!storyQuery.trim()) {
      setStoryResult("Please enter a query for the story.");
      return;
    }

    try {
      const response = await fetch("http://localhost:3001/api/story", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ query: storyQuery }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      if (data.success) {
        setStoryResult(data.story);
      } else {
        setStoryResult("No story found. Try another query.");
      }
    } catch (error) {
      console.error("Error fetching story:", error.message);
      setStoryResult("Error fetching story. Please try again.");
    }
  };


  return (
    <div className="App">
      <header>
        <h1>BibleWithAI</h1>
      </header>

      <main>
        {/* Daily Quote Section */}
        <section className="daily-quote">
          <h2>Daily Bible Quote</h2>
          <p>{dailyQuote}</p>
        </section>

        {/* Search Section */}
        <section className="search-section">
          <h2>Search Bible Verses</h2>
          <input
            type="text"
            placeholder="Enter a keyword (e.g., love, faith)"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <button onClick={handleSearch}>Search</button>
          <div className="search-results">{searchResults}</div>
        </section>

        {/* Storytelling Section */}
        <section className="story-section">
          <h2>Request a Spiritual Story</h2>
          <input
            type="text"
            placeholder="Enter a story request (e.g., Jesus heals the sick)"
            value={storyQuery}
            onChange={(e) => setStoryQuery(e.target.value)}
          />
          <button onClick={fetchStory}>Get Story</button>
          <div className="story-result">{storyResult}</div>
        </section>
      </main>
    </div>
  );
}

export default App;
